con:configuration.scriptText:
def description = context.expand( '${Create Application - xml (POST)#Response#declare namespace ns1=\'http://idm.api.rackspace.com/v1.0\'; //ns1:application[1]/@description}' )
def appName = context.expand( '${Create Application - xml (POST)#Response#declare namespace ns1=\'http://idm.api.rackspace.com/v1.0\'; //ns1:application[1]/@name}' )
def customerId = context.expand( '${Create Application - xml (POST)#Response#declare namespace ns1=\'http://idm.api.rackspace.com/v1.0\'; //ns1:application[1]/@customerId}' )

if( description != "Foundation Test App" || appName != "foundApp" || customerId != "RCN-987-654-321")
{
	assert false	
}

def rcn = context.expand( '${Get Application - xml (GET)#Response#declare namespace ns1=\'http://idm.api.rackspace.com/v1.0\'; //ns1:application[1]/@customerId}' )
if(rcn != "RCN-987-654-321")
{
	assert false	
}

