import com.eviware.soapui.support.XmlHolder

def holder = new XmlHolder( messageExchange.responseContentAsXml )
holder.namespaces["ns1"] = "http://docs.rackspacecloud.com/idm/api/v1.0"
def node = holder.getDomNode( "//ns1:unauthorized[1]" )

assert node != null

