import net.sf.*;
import net.sf.json.*;
import net.sf.json.groovy.*;
import net.sf.json.groovy.JsonSlurper;

GJson.enhanceClasses()
def jsonResp = context.expand( '${GET - List Endpoints for a User Token auth-token - json#Response}' )
def jsonObj = new JsonSlurper().parseText(jsonResp)
def endpoint = jsonObj."endpoints"
def id = jsonObj."endpoints".id
def type = jsonObj."endpoints".type
def name = jsonObj."endpoints".name
log.info endpoint.size()
log.info id
log.info type
log.info name
//assert type[0] != null
for (i=0 ; i<endpoint.size(); i++)
{ 
	assert id[i] != null
	assert type.toString() != ""
	if (name[i] == "cloudServers" || name[i] == "cloudFiles")
	{
		assert type[i] != null
	}
	assert name[i] != null
}

