def jsonObj = new JsonSlurper().parseText(jsonResp)

def tenants = jsonObj."tenants"
if(tenants != null)
{
	def id = jsonObj."tenants".id
	def name = jsonObj."tenants".name
	for (i=0; i<id.size(); i++)
	{
		log.info id[i]
		log.info name[i]
		context.testCase.setPropertyValue("tenantId"+i, id[i])
		context.testCase.setPropertyValue("tenantName"+i, name[i])
	}
}

import net.sf.*;
import net.sf.json.*;
import net.sf.json.groovy.*;
import net.sf.json.groovy.JsonSlurper;

GJson.enhanceClasses()
def jsonResp = context.expand( '${POST - Authenticate SubUser user-pwd u1 - json#Response}' )
def jsonObj = new JsonSlurper().parseText(jsonResp)
def id = jsonObj."access"."token".id
log.info id
//store admin token
assert id != null || id != ""
def roleId = jsonObj."access"."user"."roles".id
log.info roleId[0]
def roleName = jsonObj."access"."user"."roles".name
log.info roleName[0]
def roleDesc = jsonObj."access"."user"."roles".description
log.info roleDesc[0]
//store admin role info
assert roleId[0] == "2"
assert roleDesc[0] == "Default Role."

