def jsonResp = context.expand( '${POST - Update SubUser1 pwd constraint own-token u1 3 - json 400#Response}' )
def jsonObj = new JsonSlurper().parseText(jsonResp)
def bad = jsonObj."badRequest"
if (bad != null)
{
	def message = jsonObj."badRequest".message
	assert (message != "" || message != null)
	log.info message
	assert message.contains("Password is not valid: Password is too short") 
}
def user = jsonObj."user"

