def key = context.expand( '${POST - Add SubUser apiKeyCred parent-token u2 - xml#Response#declare namespace ns3=\'http://docs.rackspace.com/identity/api/ext/RAX-KSKEY/v1.0\'; //ns3:apiKeyCredentials[1]/@apiKey}' )
