import net.sf.json.*;
import net.sf.json.groovy.*;
import net.sf.json.groovy.JsonSlurper;

GJson.enhanceClasses()
def jsonResp = context.expand( '${POST - Add User auth-token - json 501#Response}' )
def jsonObj = new JsonSlurper().parseText(jsonResp)
def code = jsonObj."badRequest".code
log.info code
assert code == 501
def msg = jsonObj."badRequest".message
log.info msg
assert msg.contains("Create user not implemented.")

import net.sf.*;
import net.sf.json.*;
import net.sf.json.groovy.*;
import net.sf.json.groovy.JsonSlurper;

GJson.enhanceClasses()
def jsonResp = context.expand( '${GET - Get SubUser after delete u1 - json#Response}' )
def jsonObj = new JsonSlurper().parseText(jsonResp)
def u1SubUserId1 = context.expand( '${#TestSuite#u1SubUserId1}' )

def msg = jsonObj."itemNotFound".message
log.info msg
assert msg != null
def message = "User with id: '"+ u1SubUserId1 + "' was not found."
assert msg == message

