import net.sf.*;
import net.sf.json.*;
import net.sf.json.groovy.*;
import net.sf.json.groovy.JsonSlurper;

GJson.enhanceClasses()
def jsonResp = context.expand( '${POST - Add SubUser u1 - json#Response}' )
def jsonObj = new JsonSlurper().parseText(jsonResp)
def userId = jsonObj."user".id
def password = jsonObj."user"."OS-KSADM:password"
log.info userId
log.info password
if (userId.toString() != "")
{
	testRunner.testCase.testSuite.setPropertyValue("u1SubUserId1", userId)
	testRunner.testCase.testSuite.setPropertyValue("u1SubUserPassword1", password)
	assert password != null
}

