import net.sf.*;
import net.sf.json.*;
import net.sf.json.groovy.*;
import net.sf.json.groovy.JsonSlurper;

GJson.enhanceClasses()
def jsonResp = context.expand( '${GET - Get Users For Group 2 new - json#Response}' )
def jsonObj = new JsonSlurper().parseText(jsonResp)
def users = jsonObj."users"
def found = 0
if (users != null)
{
	def id = jsonObj."users".id
	def name = jsonObj."users".name
	for (i=0; i<id.size(); i++)
	{
		if (id[i] == context.expand('${#TestSuite#userId2}'))
		{
			found = found+1
		}
		if (id[i] == context.expand('${#TestSuite#u2SubUserId1'))
		{
			found = found+1
		}
	}
	assert found == true
}

def name1 = context.expand( '${GET - Get Users For Group 2 new - xml#Response#declare namespace ns2=\'http://docs.openstack.org/identity/api/v2.0\'; //ns2:users[1]/ns2:user[@id="${#TestSuite#userId2}"]/@username}' )
def name2 = context.expand( '${GET - Get Users For Group 2 new - xml#Response#declare namespace ns2=\'http://docs.openstack.org/identity/api/v2.0\'; //ns2:users[1]/ns2:user[@id="${#TestSuite#u2SubUserId1}"]/@username}' )
log.info name1
log.info name2
def username2 = context.expand( '${#TestSuite#username2}' )
def u2SubUserName1 = context.expand( '${#TestSuite#u2SubUserName1}' )
log.info username2
log.info u2SubUserName1

assert name1 == username2
assert name2 == u2SubUserName1

def name = context.expand( '${GET - Get Users For Group 4 after added - xml#Response#declare namespace ns2=\'http://docs.openstack.org/identity/api/v2.0\'; //ns2:users[1]/ns2:user[@id="${#TestSuite#userId}"]/@username}' )
log.info name
assert name.toString() == context.expand( '${#testSuite#username}' )

def name = context.expand( '${GET - Get Users For Group 4 after delete - xml#Response#declare namespace ns2=\'http://docs.openstack.org/identity/api/v2.0\'; //ns2:users[1]/ns2:user[@id="${#TestSuite#userId}"]/@username}' )
log.info name
def msg = context.expand( '${GET - Get Users For Group 4 after delete - xml#Response#declare namespace ns2=\'http://docs.openstack.org/identity/api/v2.0\'; //ns2:itemNotFound[1]/ns2:message[1]}' )
log.info msg
assert (name.toString() == "" || msg.contains("No users in group"))

