import net.sf.json.*;
import net.sf.json.groovy.*;
import net.sf.json.groovy.JsonSlurper;

GJson.enhanceClasses()
def jsonResp = context.expand( '${GET - Get Role w id - json 404#Response}' )
def jsonObj = new JsonSlurper().parseText(jsonResp)
def fault = jsonObj."itemNotFound"
if (fault != null)
{
	def code = jsonObj."itemNotFound".code
	def msg = jsonObj."itemNotFound".message
	assert code == 404
	assert msg.contains("Role with id: '"+context.expand( '${#TestCase#testRoleId}' )+"' was not found.")
}

import net.sf.*;
import net.sf.json.*;
import net.sf.json.groovy.*;
import net.sf.json.groovy.JsonSlurper;

GJson.enhanceClasses()
def jsonResp = context.expand( '${POST - Add Role - json#Response}' )
def jsonObj = new JsonSlurper().parseText(jsonResp)
def id = jsonObj."role".id
def name = jsonObj."role".name
def desc = jsonObj."role".description
def role = jsonObj."role"
assert role.size() == 3
if (role != null)
{
	context.testCase.setPropertyValue("newRoleId", id)
	context.testCase.setPropertyValue("newRoleName", name)
	context.testCase.setPropertyValue("newRoleDesc", desc)
}
else
{
	context.testCase.setPropertyValue("newRoleId", "")
	context.testCase.setPropertyValue("newRoleName", "")
	context.testCase.setPropertyValue("newRoleDesc", "")
}

import net.sf.*;
import net.sf.json.*;
import net.sf.json.groovy.*;
import net.sf.json.groovy.JsonSlurper;

GJson.enhanceClasses()
def jsonResp = context.expand( '${GET - List Roles - json#Response}' )
def jsonObj = new JsonSlurper().parseText(jsonResp)
def roles = jsonObj."roles"
def id = jsonObj."roles".id
def name = jsonObj."roles".name
def desc = jsonObj."roles".description
log.info id.size()
for (i=0; i<id.size(); i++)
{
	log.info id[i]
	log.info name[i]
	log.info desc[i]
	assert id[i] != null
	assert name[i] != null
}
//verify valid fields
log.info roles.size()
for (f=0; f<roles.size(); f++)
{
	assert roles[f].size() == 3
	log.info roles[f].size()
}

