GJson.enhanceClasses()
def jsonResp = context.expand( '${GET - List Roles - json#Response}' )
def jsonObj = new JsonSlurper().parseText(jsonResp)
def roles = jsonObj."roles"
def id = jsonObj."roles".id
def name = jsonObj."roles".name
def desc = jsonObj."roles".description
log.info id.size()
for (i=0; i<id.size(); i++)
{
	log.info id[i]
	log.info name[i]
	log.info desc[i]
	assert id[i] != null
	assert name[i] != null
	context.testCase.setPropertyValue("roleId"+i, id[i])
	context.testCase.setPropertyValue("roleName"+i, name[i])
	context.testCase.setPropertyValue("roleDesc"+i, desc[i])
}
//verify valid fields
log.info roles.size()
for (f=0; f<roles.size(); f++)
{
	assert roles[f].size() == 3
	log.info roles[f].size()
}

