	assert name[i] != null
	//context.testCase.setPropertyValue("roleId"+i, id[i])
	//context.testCase.setPropertyValue("roleName"+i, name[i])
	//context.testCase.setPropertyValue("roleDesc"+i, desc[i])
}
//verify valid fields
log.info roles.size()
for (f=0; f<roles.size(); f++)
{
	assert roles[f].size() == 3
	log.info roles[f].size()
}

