import net.sf.*;
import net.sf.json.*;
import net.sf.json.groovy.*;
import net.sf.json.groovy.JsonSlurper;

GJson.enhanceClasses()
def jsonResp = context.expand( '${POST - Add Role - json#Response}' )
def jsonObj = new JsonSlurper().parseText(jsonResp)
def id = jsonObj."role".id
def name = jsonObj."role".name
def desc = jsonObj."role".description
def role = jsonObj."role"
assert role.size() == 3
if (role != null)
{
	context.testCase.setPropertyValue("newRoleId", id)
	context.testCase.setPropertyValue("newRoleName", name)
	context.testCase.setPropertyValue("newRoleDesc", desc)
}
else
{
	context.testCase.setPropertyValue("newRoleId", "")
	context.testCase.setPropertyValue("newRoleName", "")
	context.testCase.setPropertyValue("newRoleDesc", "")
}

def id = context.expand( '${POST - Add Role - xml#Response#declare namespace ns2=\'http://docs.openstack.org/identity/api/v2.0\'; //ns2:role[1]/@id}' )
def name = context.expand( '${POST - Add Role - xml#Response#declare namespace ns2=\'http://docs.openstack.org/identity/api/v2.0\'; //ns2:role[1]/@name}' )
def desc = context.expand( '${POST - Add Role - xml#Response#declare namespace ns2=\'http://docs.openstack.org/identity/api/v2.0\'; //ns2:role[1]/@description}' )

if (id.toString() != "")
{
	context.testCase.setPropertyValue("newRoleId", id)
	context.testCase.setPropertyValue("newRoleName", name)
	context.testCase.setPropertyValue("newRoleDesc", desc)
}
else
{
	context.testCase.setPropertyValue("newRoleId", "")
	context.testCase.setPropertyValue("newRoleName", "")
	context.testCase.setPropertyValue("newRoleDesc", "")
}

