def role = jsonObj."role"
assert role.size() == 3
def id = jsonObj."role".id
def name = jsonObj."role".name
def desc = jsonObj."role".description
assert id == context.expand('${#TestCase#newRoleId}')
assert name == context.expand('${#TestCase#newRoleName}')
assert desc == context.expand('${#TestCase#newRoleDesc}')

